@prefix sm: 	<http://www.smartmaas.de/sm-ns#> .
@prefix tio:	<http://purl.org/tio/ns#> .
@prefix rdf: 	<http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: 	<http://www.w3.org/2000/01/rdf-schema#> .
@prefix time: 	<http://www.w3.org/2006/time#> .
@prefix gr:     <http://purl.org/goodrelations/v1#> .

sm:linie124 a tio:CoachService ;
	tio:from <http://resource.for/SaarVV_Uni_Campus> ;
	tio:to <http://resource.for/SaarVV_Hauptbahnhof> .

sm:ticket1 a tio:TicketPlaceholder ;
	rdfs:label "Busticket von Universit√§t zum Hauptbahnhof"@de ;
	tio:accessTo sm:linie124 .

sm:SaarVV gr:offers sm:offer1 .

sm:offer1 a gr:offering ;
	gr:name "Tagesticket" ;
	gr:includes sm:ticket1 ;
	gr:validFrom "2019-04-01T08:00:00"^^xsd:dateTime ;
	gr:validThrough "2019-04-02T08:00:00"^^xsd:dateTime .

@prefix : <http://www.smart-maas.eu/#> .
@prefix wsdl: <https://www.w3.org/ns/wsdl20-rdf#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:SmartMaaSServiceDescription a wsdl:Description .

:SmartMaaSTicketservice a wsdl:service ;
	wsdl:endpoint <http://localhost:8083/ticket> ;
	:hasTemplate <http://localhost:8801/serviceTemplate?filename=json_schema_template.json> .

@prefix rr: <http://www.w3.org/ns/r2rml#>.
@prefix pt: 	<http://www.public-transport-example.ex/ns#> .
@prefix tio: <http://purl.org/tio/ns#> .
@prefix rml: <http://semweb.mmlab.be/ns/rml#>.
@prefix ql: <http://semweb.mmlab.be/ns/ql#>.
@prefix transit: <http://vocab.org/transit/terms/>.
@prefix xsd: <http://www.w3.org/2001/XMLSchema#>.
@prefix wgs84_pos: <http://www.w3.org/2003/01/geo/wgs84_pos#>.
@prefix rdfs: 	<http://www.w3.org/2000/01/rdf-schema#> .
@prefix carml: <http://carml.taxonic.com/carml/> .

<#BusLineMapping>
  rml:logicalSource [
    rml:source [
		a carml:Stream ;
	] ;
    rml:referenceFormulation ql:JSONPath;
	rml:iterator "$.connection.value.line"
  ];

  rr:subjectMap [
    rr:template "http://www.public-transport-example.ex/ns#{$}";
    rr:class tio:CoachService
  ];

   rr:predicateObjectMap [
    rr:predicate tio:from;
    rr:objectMap [
      rml:reference "$.connection.value.from";
      rr:datatype xsd:string
      ]
    ];

	 rr:predicateObjectMap [
    rr:predicate tio:to;
    rr:objectMap [
      rml:reference "$.connection.value.to";
      rr:datatype xsd:string
      ]
    ].

<#TicketTypeMapping>
 rml:logicalSource [
    rml:source [
		a carml:Stream ;
	] ;
    rml:referenceFormulation ql:JSONPath;
	rml:iterator "$"
  ];

   rr:subjectMap [
    rr:template "http://www.public-transport-example.ex/ns#ticket1";
    rr:class tio:TicketPlaceholder
  ];

  rr:predicateObjectMap [
    rr:predicate rdfs:label;
    rr:objectMap [
      rml:reference "$.type";
      rr:datatype xsd:string
      ]
    ];

	rr:predicateObjectMap [
    rr:predicate tio:accessTo;
    rr:objectMap [
      rml:reference "http://www.public-transport-example.ex/ns#{$.connection.value.line}";
      ]
    ].
<#OfferMapping>
 rml:logicalSource [
    rml:source [
		a carml:Stream ;
	] ;
    rml:referenceFormulation ql:JSONPath;
	rml:iterator "$"
  ];
  rr:subjectMap [
    rr:template "http://www.public-transport-example.ex/ns#SaarVV";
    rr:class gr:offers
  ];
   rr:predicateObjectMap [
    rr:predicate gr:name;
    rr:object "offer1"
	].
<#ConnectionMapping>
 rml:logicalSource [
    rml:source [
		a carml:Stream ;
	] ;
    rml:referenceFormulation ql:JSONPath;
	rml:iterator "$"
  ];
	rr:subjectMap [
    rr:template "http://www.public-transport-example.ex/ns#offer1";
    rr:class gr:offering
  ];

  rr:predicateObjectMap [
    rr:predicate gr:name;
    rr:objectMap [
      rml:reference "http://www.public-transport-example.ex/ns#{$.type}";
      rr:datatype xsd:string
      ]
	];

	  rr:predicateObjectMap [
    rr:predicate gr:includes;
    rr:objectMap [
      rml:reference "pt:ticket1";
      rr:datatype xsd:string
      ]
    ];

	rr:predicateObjectMap [
    rr:predicate gr:validFrom;
    rr:objectMap [
      rml:reference "$.connection.validity.from";
      rr:datatype xsd:dateTime
      ]
    ];

	rr:predicateObjectMap [
    rr:predicate gr:validThrough;
    rr:objectMap [
      rml:reference "$.connection.validity.to";
      rr:datatype xsd:dateTime
      ]
    ].
